import React, { useState } from 'react'
import "./Contact.css";


const Contact=()=>{
const [data,setdata]=useState({
  name:"",phone:'',email:'',message:'',
});

let name,value;
const EventSubmit=(event)=>{
  // console.log(event)
name=event.target.name;
value=event.target.value;

setdata({...data,[name]:value});
}

  return(
    <>
    <div className='my-5'>
      <h1 className='text-center'>Contact Us</h1>
    </div>
    <div className='container mb-5'>
      <div className='row'>
        <div className='col-md-6 col-10 mx-auto'>
          
          <form action="">
          <div className="mb-3">
  <label  className="form-label" htmlFor='name'>Name</label>
  <input type="text" className="form-control" name='name' onChange={EventSubmit} id="exampleFormControlInput1" placeholder="Enter Your Name" />
</div>
<div className="mb-3">
  <label  className="form-label" htmlFor='phone'>Phone Number</label>
  <input type="text" className="form-control" name='phone' onChange={EventSubmit} id="exampleFormControlInput2" placeholder="Enter Phone Number" />
</div>
<div className="mb-3">
  <label  className="form-label" htmlFor='email'>Email address</label>
  <input type="email" className="form-control" name='email' onChange={EventSubmit} id="exampleFormControlInput3" placeholder="Enter valid mail" />
</div>
<div className="mb-3">
  <label className="form-label" htmlFor='message'>Message</label>
  <textarea className="form-control" name='message' onChange={EventSubmit} id="exampleFormControlTextarea4" rows="3" placeholder='Write a message'></textarea>
</div>
<div className="col-12">
    <button className="btn btn-outline-primary" type="submit">Submit form</button>
  </div>
          </form>

        </div>
      </div>
    </div>
    </>
    )
}
export default Contact;
